//import store from "../store";

export const getCoursesState = (store) => store.course;

export const getApiCourses = (store) =>
  getCoursesState(store) ? getCoursesState(store).items : [];

export const getCourses = (store) =>
  getCoursesState(store) ? [...getCoursesState(store).item] : [];

//TODO: clean this up
export const getUi = (store) => store.ui;
