//import store from "../store";

export const getCoursesState = (store) => store.course;

export const getApiCourses = (store) =>
  getCoursesState(store) ? getCoursesState(store).items : [];

export const getNewCourses = (store) =>
  getCourseState(store) ? getCoursesState(store).item : [];

export const getCourseById = (store, id) =>
  getCoursesState(store) ? { ...getCoursesState(store).items[id], id } : {};

export const getCourseState = (store) => store.course;

export const getCourses = (store) =>
  getCourseState(store) ? [...getCoursesState(store)] : [];
//TODO: clean this up
export const getUi = (store) => store.ui;
