import React, { Component } from "react";
import Course from "./course";
import { createStore, applyMiddleware } from "redux";
import { connect } from "react-redux";
import { fetchCourses } from "../actions/courseActions";
import store from "../store";

class Courses extends Component {
  toggleCourseDisplayed() {
    const toggle = !this.state.coursesDisplayed;
    this.setState({ coursesDisplayed: toggle });
  }
  componentWillMount() {
    fetchCourses();
  }
  render() {
    return (
      <div>
        <button onClick={this.toggleCourseDisplayed}>
          {this.state.coursesDisplayed
            ? "Stop Displaying Courses"
            : "Display Courses"}
        </button>
        {this.state.coursesDisplayed ? courseItems : null}
      </div>
    );
  }
}

const mapStateToProps = (state) => state.courses;

export default connect(mapStateToProps)(Courses);
