import * as actions from "./types";

export const fetchCourses = () => (dispatch) => {
  fetch("https://jsonplaceholder.typicode.com/posts")
    .then((res) => res.json())
    .then((courses) =>
      dispatch({
        type: actions.FETCH_COURSES,
        payload: courses,
      })
    );
};

export const addCourse = (course) => (dispatch) => {
  dispatch({
    type: actions.NEW_COURSE,
    payload: course,
  });
};
