import { FETCH_COURSES, NEW_COURSE } from "./types";

export const fetchCourses = () => (dispatch) => {
  fetch("/courses")
    .then((res) => res.json())
    .then((courses) =>
      dispatch({
        type: FETCH_COURSES,
        payload: courses,
      })
    );
};

export const addCourse = (course) => (dispatch) => {
  fetch("/courses/newCourse", {
    method: "POST",
    headers: {
      "Content-Type": "application/json;charset=utf-8",
    },
    body: JSON.stringify(user),
  })
    .then((res) => res.json())
    .then((courses) =>
      dispatch({
        type: FETCH_COURSES,
        payload: courses,
      })
    );
};
dispatch({
  type: NEW_COURSE,
  payload: { ...course },
});
