import React, { Component } from "react";
import Course from "./course";

import { connect } from "react-redux";
import { fetchCourses } from "../actions/courseActions";

import { getCourses } from "../actions/selectors";

class Courses extends Component {
  componentDidMount() {
    //this.props.fetchCourses();//this should prevent API from running
    console.log(this.props.courses);
  }

  render() {
    const courseItems = this.props.courses.map((courses, index) => (
      <Course key={courses.id} title={courses.title} body={courses.body} />
    ));
    return <div>{courseItems}</div>;
  }
}

export default connect((state) => ({ ...getCourses(state) }), {
  fetchCourses,
})(Courses);
