import React, { Component } from "react";
import CourseCreator from "./components/courseCreator";
import Courses from "./components/courses";
import "./App.css";
import { connect } from "react-redux";
import { getUi } from "./actions/selectors";
import { toggleCourseCreate, toggleCourseDisplay } from "./actions/uiActions";

class App extends Component {
  toggleCourseCreator() {
    const toggle = !this.props.ui.courseCreatorDisplayed;
    this.props.toggleCourseCreate(toggle);
  }
  toggleCourseDisplay() {
    const toggle = !this.props.ui.coursesDisplayed;
    this.props.toggleCourseDisplay(toggle);
  }

  render() {
    return (
      <div>
        <button onClick={this.toggleCourseCreator}>
          {this.state.courseCreatorOpen
            ? "Stop Create Course"
            : "Create Course"}
        </button>
        {this.state.courseCreatorOpen ? <CourseCreator /> : null}
        <br />
        <Courses />
      </div>
    );
  }
}
//id: 1, title: "<h3>Title</h3>", body: "<div>Body</div>" this may come in usefull

export default connect((state) => ({ ui: getUi(state) }), {
  toggleCourseCreate,
  toggleCourseDisplay,
})(App);
