import { FETCH_COURSES, NEW_COURSE } from "../actions/types";

const initialState = {
  items: [],
  item: {
    body: "",
    title: "",
  },
};

export default function (state = initialState, action) {
  switch (action.type) {
    case FETCH_COURSES:
      return { ...state, items: action.payload };
    case NEW_COURSE:
      return { ...state, item: action.payload };
    default:
      return state;
  }
}
