import { FETCH_COURSES, NEW_COURSE } from "../actions/types";

const initialState = {
  items: [],
};
const initialId = 100;
export default function (state = initialState, action) {
  switch (action.type) {
    case FETCH_COURSES:
      return { ...state, items: action.payload };
    case NEW_COURSE:
      return { item: { ...action.payload, id: 120 }, ...state };
    default:
      return state;
  }
}
