import React, { Component } from "react";
import logo from "./logo.svg";
import CourseCreator from "./components/courseCreator";
import Course from "./components/course";
import "./App.css";
import axios from "axios";

class App extends Component {
  state: {
    courses: [
      { id: 1, title: "<h3>Title</h3>", body: "<div>Body</div>" },
      { id: 2, title: "<h3>Title</h3>", body: "<div>Body</div>" },
      { id: 3, title: "<h3>Title</h3>", body: "<div>Body</div>" },
      { id: 4, title: "<h3>Title</h3>", body: "<div>Body</div>" }
    ],
  };
  componentWillMount() {
    fetch("/api/courses")
      .then((res) => res.json())
      .then((data) => console.log(data));
  }
  render() {
    const courseItems = this.state.courses.map((courses) => (
      <Course
        id={this.state.courses.id}
        title={this.state.courses.title}
        body={this.state.courses.body}
      />
    ));
    return (
      <div>
        <button>Create Course</button>
        <CourseCreator />
        <br />
        <button>Display Courses</button>
        {courseItems}
      </div>
    );
  }
}

export default App;
