import { FETCH_COURSES, NEW_COURSE } from "./types";

export const fetchCourses = () => (dispatch) => {
  return function (dispatch) {
    fetch("https://jsonplaceholder.typicode.com/posts")
      .then((res) => res.json())
      .then((courses) => {
        dispatch({
          type: FETCH_COURSES,
          payload: courses,
        });
      });
  };
};

export function addCourse() {
  return function (dispatch) {
    dispatch({
      type: NEW_COURSE,
      payload: course,
    });
  };
}
