import React, { Component } from "react";
import { connect } from "react-redux";
import { fetchCourses } from "./actions/courseActions";
import logo from "./logo.svg";
import CourseCreator from "./components/courseCreator";
import Course from "./components/course";
import "./App.css";
import { Provider } from "react-redux";
import { createStore, applyMiddleware } from "redux";
import store from "./store";

class App extends Component {
  constructor(props) {
    super(props);
    this.toggleCourseCreator = this.toggleCourseCreator.bind(this);
    this.toggleCourseDisplayed = this.toggleCourseDisplayed.bind(this);
    this.state = {
      courseCreatorOpen: false,
      coursesDisplayed: true,
    };
  }

  toggleCourseCreator() {
    const toggle = !this.state.courseCreatorOpen;
    this.setState({ courseCreatorOpen: toggle });
  }
  toggleCourseDisplayed() {
    const toggle = !this.state.coursesDisplayed;
    this.setState({ coursesDisplayed: toggle });
  }
  render() {
    const courseItems = this.props.courses.map((courses) => (
      <Course key={courses.id} title={courses.title} body={courses.body} />
    ));
    return (
      <Provider store={store}>
        <div>
          <button onClick={this.toggleCourseCreator}>
            {this.state.courseCreatorOpen
              ? "Stop Create Course"
              : "Create Course"}
          </button>
          {this.state.courseCreatorOpen ? <CourseCreator /> : null}
          <br />
          <button onClick={this.toggleCourseDisplayed}>
            {this.state.coursesDisplayed
              ? "Stop Displaying Courses"
              : "Display Courses"}
          </button>
          {this.state.coursesDisplayed ? courseItems : null}
        </div>
      </Provider>
    );
  }
}
//id: 1, title: "<h3>Title</h3>", body: "<div>Body</div>" this may come in usefull

export default App;
