import { FETCH_COURSES, NEW_COURSE } from "./types";
import axios from "axios";

export const fetchCourses = () => (dispatch) => {
  axios
    .get("/courses")

    .then((courses) =>
      dispatch({
        type: FETCH_COURSES,
        payload: courses,
      })
    )
    .catch((err) => console.log(err));
};

export const addCourse = (course) => (dispatch) => {
  dispatch({
    type: NEW_COURSE,
    payload: { ...course },
  });
};
