import React, { Component } from "react";
import logo from "./logo.svg";
import CourseCreator from "./components/courseCreator";
import Course from "./components/course";
import "./App.css";
import axios from "axios";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      courses: [],
    };
  }
  componentWillMount() {
    fetch("/api/courses")
      .then((res) => res.json())
      .then((data) => console.log(data));
  }
  render() {
    const courseItems = this.state.courses.map((courses) => (
      <Course id={courses.id} title={courses.title} body={courses.body} />
    ));
    return (
      <div>
        <button>Create Course</button>
        <CourseCreator />
        <br />
        <button>Display Courses</button>
        {courseItems}
      </div>
    );
  }
}
//id: 1, title: "<h3>Title</h3>", body: "<div>Body</div>" this may come in usefull
export default App;
