import { FETCH_COURSES, NEW_COURSE } from "../actions/types";

const initialState = {
  items: [],
  item: [],
  coursesDisplayed: false,
  courseCreatorDisplayed: false,
};
let initialId = 100;
export default function (state = initialState, action) {
  switch (action.type) {
    case FETCH_COURSES:
      return { ...state, items: action.payload };
    case NEW_COURSE:
      return { ...state, item: [{ ...action.payload, id: ++initialId }] };

    case TOGGLE_CREATE_COURSE:
      return {};
    default:
      return state;
  }
}
