import React, { Component } from "react";
import Card from "react-bootstrap/Card";

class Course extends Component {
  render() {
    const str = this.props.body;
    return (
      <Card>
        <Card.Body>
          <Card.Title>{this.props.title}</Card.Title>
          {str.replace(/^"(.*)"$/, "$1")}
        </Card.Body>
      </Card>
    );
  }
}

export default Course;
