import React, { Component } from "react";
import Course from "./course";

import { connect } from "react-redux";
import { fetchCourses } from "../actions/courseActions";

import { getCourses } from "../actions/selectors";

class Courses extends Component {
  componentDidMount() {
    //this.props.fetchCourses();//this should prevent API from running
  }

  render() {
    const courseItems = this.props.courses.map((courses) => (
      <Course key={courses.id} title={courses.title} body={courses.body} />
    ));
    return (
      <div>
        <button onClick={this.toggleCourseDisplayed}>
          {this.state.coursesDisplayed
            ? "Stop Displaying Courses"
            : "Display Courses"}
        </button>
        {this.state.coursesDisplayed ? courseItems : null}
      </div>
    );
  }
}

export default connect((state) => ({ courses: getCourses(state) }), {
  fetchCourses,
})(Courses);
