export const getCourseById = (store, id) => {
  store.course.item ? store.course.item[id] : {};
};

export const getCourses = (store) => {
  if (store.course) {
    const array = store.course.item;
    courses = array.map((id) => getCourseById(store, id));
    return courses;
  }
  return [];
};

export const getUi = (store) => store.ui;
