import { FETCH_COURSES, NEW_COURSE } from "../actions/types";

const initialState = {
  items: [],
  item: [],
};
let initialId = 0;
export default function (state = initialState, action) {
  switch (action.type) {
    case FETCH_COURSES:
      return { ...state, items: action.payload };
    case NEW_COURSE: {
      const { content, id } = action.payload;
      const course = { ...content, key: id };
      return {
        ...state,
        item: { ...state.item, course }, //this needs to be an array so that i can map interval
      };
    }

    default:
      return state;
  }
}
