import React, { Component } from "react";
import logo from "./logo.svg";
import CourseCreator from "./components/courseCreator";
import Course from "./components/course";
import "./App.css";
import axios from "axios";

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      courseCreatorOpen: false,
      coursesDisplayed: true,
      courses: [],
    };
  }
  componentWillMount() {
    fetch("/api/courses")
      .then((res) => res.json())
      .then((data) => this.setState({ courses: data }));
  }
  toggleCourseCreator() {
    this.setState(!courseCreatorOpen);
  }
  render() {
    const courseItems = this.state.courses.map((courses) => (
      <Course id={courses.id} title={courses.title} body={courses.body} />
    ));
    return (
      <div>
        <button onClick={this.toggleCourseCreator}>Create Course</button>
        <CourseCreator />
        <br />
        <button>Display Courses</button>
        {courseItems}
      </div>
    );
  }
}
