import * as typpes from "./types";

export const fetchCourses = () => (dispatch) => {
  fetch("https://jsonplaceholder.typicode.com/posts")
    .then((res) => res.json())
    .then((courses) =>
      dispatch({
        type: types.FETCH_COURSES,
        payload: courses,
      })
    );
};

export const addCourse = (course) => (dispatch) => {
  dispatch({
    type: types.NEW_COURSE,
    payload: course,
  });
};

