import { FETCH_COURSES, NEW_COURSE } from "../actions/types";

const initialState = {
  items: [],
  item: [],
};
let initialId = 0;
export default function (state = initialState, action) {
  switch (action.type) {
    case FETCH_COURSES:
      return { ...state, items: action.payload };
    case NEW_COURSE:
      const { content, id } = action.payload;
      return {
        ...state,
        item: { ...state.item, [id]: content },
      };

    default:
      return state;
  }
}
