export const getCoursesState = (store) => store.course;

export const getCourses = () =>
  getCoursesState(store) ? getCoursesState(store).items : [];

export const getCourseById = (store, id) =>
  getCoursesState(store) ? { ...getCoursesState(store).items[id], id } : {};

export const getCourselist = (store) =>
  getCourses(store).map((id) => getCourseById(store, id));
